mapper.select:
SELECT COUNT(*) FROM USER_COUPON
WHERE USER_ID = #{userId}
  AND CPN_SEQ = #{cpnSeq}
  AND NVL(USER_CPN_IS_USED, 'N') = 'N'

mapper.update:
UPDATE USER_COUPON
SET USER_CPN_IS_USED = 'Y',
    USAGE_DT = SYSDATE
WHERE USER_ID = #{userId}
  AND CPN_SEQ = #{cpnSeq}
  AND USER_CPN_IS_USED = 'N'

mapper.select:
SELECT COUNT(USER_CPN_SEQ) CNT
  FROM USER_COUPON
 WHERE USER_ID = #{value}
