mapper.select:
SELECT HOST_ID 
FROM ROOM 
WHERE ROOM_SEQ = #{roomSeq}

SELECT COUNT(ROOM_SEQ) CNT FROM ( SELECT ROWNUM RNUM, INNER.* FROM ( SELECT ROOM_SEQ, NVL(A.ROOM_CAT_SEQ, 0) ROOM_CAT_SEQ, HOST_ID, NVL(ROOM_ADDR, '') ROOM_ADDR, NVL(LATITUDE, 0) LATITUDE, NVL(LONGITUDE, 0) LONGITUDE, NVL(REGION, '') REGION, TO_CHAR(REG_DT, 'YYYY.MM.DD HH24:MI:SS') REG_DT, TO_CHAR(UPDATE_DT, 'YYYY.MM.DD HH24:MI:SS') UPDATE_DT, NVL(AUTO_CONFIRM_YN, '') AUTO_CONFIRM_YN, NVL(ROOM_TITLE, '') ROOM_TITLE, NVL(ROOM_DESC, '') ROOM_DESC, NVL(CANCEL_POLICY, '') CANCEL_POLICY, NVL(AVERAGE_RATING, 0) AVERAGE_RATING, NVL(REVIEW_COUNT, 0) REVIEW_COUNT, NVL(MIN_TIMES, 0) MIN_TIMES, NVL(MAX_TIMES, 0) MAX_TIMES FROM ROOM A, ROOM_CATEGORY B WHERE A.ROOM_CAT_SEQ = B.ROOM_CAT_SEQ AND A.ROOM_CAT_SEQ IN(8,9,10,11,12,13,14) AND A.ROOM_SEQ IN (SELECT ROOM_SEQ FROM ROOM_TYPE WHERE TO_DATE(#{endDate}, 'YYYYMMDD') - TO_DATE(#{startDate}, 'YYYYMMDD') >= MIN_DAY AND TO_DATE(#{endDate}, 'YYYYMMDD') - TO_DATE(#{startDate}, 'YYYYMMDD') <= MAX_DAY)
AND ( ROOM_TITLE LIKE '%' || #{searchValue} || '%' OR ROOM_ADDR LIKE '%' || #{searchValue} || '%' OR DBMS_LOB.INSTR(ROOM_DESC, #{searchValue}) > 0 )
AND REGION = #{regionList}
AND ROOM_CAT_NAME = #{category}
AND A.ROOM_SEQ IN (SELECT ROOM_SEQ FROM ROOM_TYPE WHERE MAX_GUESTS >= #{personCount})
AND ( SELECT ROUND(SUM( CASE WHEN TO_CHAR(DATES2.DAY, 'DY', 'NLS_DATE_LANGUAGE=AMERICAN') IN ('FRI','SAT','SUN') THEN RT2.WEEKEND_AMT ELSE RT2.WEEKDAY_AMT END ) / COUNT(*), 0) FROM ( SELECT TO_DATE(#{startDate},'YYYYMMDD') + LEVEL - 1 AS DAY FROM DUAL CONNECT BY LEVEL <= TO_DATE(#{endDate},'YYYYMMDD') - TO_DATE(#{startDate},'YYYYMMDD') ) DATES2 , ROOM_TYPE RT2 WHERE RT2.ROOM_TYPE_SEQ = ( SELECT ROOM_TYPE_SEQ FROM ( SELECT ROOM_TYPE_SEQ FROM ROOM_TYPE WHERE ROOM_SEQ = A.ROOM_SEQ ORDER BY WEEKDAY_AMT ASC ) WHERE ROWNUM = 1 ) )
BETWEEN #{minPrice} AND #{maxPrice}
>= #{minPrice}
<= #{maxPrice}
AND (
EXISTS ( SELECT 1 FROM ROOM_FACILITY RF JOIN FACILITY F ON RF.FAC_SEQ = F.FAC_SEQ WHERE RF.ROOM_SEQ = A.ROOM_SEQ AND F.FAC_NAME = #{fac} )
)
ORDER BY ROOM_SEQ DESC ) INNER ) OUTER WHERE 1 = 1

SELECT ROOM_SEQ, ROOM_CAT_SEQ, HOST_ID, ROOM_ADDR, LATITUDE, LONGITUDE, REGION, REG_DT, UPDATE_DT, AUTO_CONFIRM_YN, ROOM_TITLE, ROOM_DESC, CANCEL_POLICY, AVERAGE_RATING, REVIEW_COUNT, MIN_TIMES, MAX_TIMES, (SELECT ROOM_IMG_NAME FROM ROOM_IMAGE WHERE ROOM_SEQ = OUTER.ROOM_SEQ AND IMG_TYPE = 'main') ROOM_IMAGE_NAME, (SELECT ROUND(SUM( CASE WHEN TO_CHAR(DATES.DAY, 'DY', 'NLS_DATE_LANGUAGE=AMERICAN') IN ('FRI','SAT','SUN') THEN RT.WEEKEND_AMT ELSE RT.WEEKDAY_AMT END ) / COUNT(*), 0) FROM ( SELECT TO_DATE(#{startDate}, 'YYYYMMDD') + LEVEL - 1 AS DAY FROM DUAL CONNECT BY LEVEL <= TO_DATE(#{endDate}, 'YYYYMMDD') - TO_DATE(#{startDate}, 'YYYYMMDD') ) DATES, ROOM_TYPE RT WHERE RT.ROOM_TYPE_SEQ = ( SELECT ROOM_TYPE_SEQ FROM ( SELECT ROOM_TYPE_SEQ FROM ROOM_TYPE WHERE ROOM_SEQ = OUTER.ROOM_SEQ ORDER BY WEEKDAY_AMT ASC ) WHERE ROWNUM = 1 ) ) AS AMT FROM ( SELECT ROWNUM RNUM, INNER.* FROM ( SELECT ROOM_SEQ, NVL(A.ROOM_CAT_SEQ, 0) ROOM_CAT_SEQ, HOST_ID, NVL(ROOM_ADDR, '') ROOM_ADDR, NVL(LATITUDE, 0) LATITUDE, NVL(LONGITUDE, 0) LONGITUDE, NVL(REGION, '') REGION, TO_CHAR(REG_DT, 'YYYY.MM.DD HH24:MI:SS') REG_DT, TO_CHAR(UPDATE_DT, 'YYYY.MM.DD HH24:MI:SS') UPDATE_DT, NVL(AUTO_CONFIRM_YN, '') AUTO_CONFIRM_YN, NVL(ROOM_TITLE, '') ROOM_TITLE, NVL(ROOM_DESC, '') ROOM_DESC, NVL(CANCEL_POLICY, '') CANCEL_POLICY, NVL(AVERAGE_RATING, 0) AVERAGE_RATING, NVL(REVIEW_COUNT, 0) REVIEW_COUNT, NVL(MIN_TIMES, 0) MIN_TIMES, NVL(MAX_TIMES, 0) MAX_TIMES FROM ROOM A, ROOM_CATEGORY B WHERE A.ROOM_CAT_SEQ = B.ROOM_CAT_SEQ AND A.ROOM_CAT_SEQ IN(8,9,10,11,12,13,14) AND A.ROOM_SEQ IN (SELECT ROOM_SEQ FROM ROOM_TYPE WHERE TO_DATE(#{endDate}, 'YYYYMMDD') - TO_DATE(#{startDate}, 'YYYYMMDD') >= MIN_DAY AND TO_DATE(#{endDate}, 'YYYYMMDD') - TO_DATE(#{startDate}, 'YYYYMMDD') <= MAX_DAY)
AND ( ROOM_TITLE LIKE '%' || #{searchValue} || '%' OR ROOM_ADDR LIKE '%' || #{searchValue} || '%' OR DBMS_LOB.INSTR(ROOM_DESC, #{searchValue}) > 0 )
AND REGION = #{regionList}
AND ROOM_CAT_NAME = #{category}
AND A.ROOM_SEQ IN (SELECT ROOM_SEQ FROM ROOM_TYPE WHERE MAX_GUESTS >= #{personCount})
AND ( SELECT ROUND(SUM( CASE WHEN TO_CHAR(DATES2.DAY, 'DY', 'NLS_DATE_LANGUAGE=AMERICAN') IN ('FRI','SAT','SUN') THEN RT2.WEEKEND_AMT ELSE RT2.WEEKDAY_AMT END ) / COUNT(*), 0) FROM ( SELECT TO_DATE(#{startDate},'YYYYMMDD') + LEVEL - 1 AS DAY FROM DUAL CONNECT BY LEVEL <= TO_DATE(#{endDate},'YYYYMMDD') - TO_DATE(#{startDate},'YYYYMMDD') ) DATES2 , ROOM_TYPE RT2 WHERE RT2.ROOM_TYPE_SEQ = ( SELECT ROOM_TYPE_SEQ FROM ( SELECT ROOM_TYPE_SEQ FROM ROOM_TYPE WHERE ROOM_SEQ = A.ROOM_SEQ ORDER BY WEEKDAY_AMT ASC ) WHERE ROWNUM = 1 ) )
BETWEEN #{minPrice} AND #{maxPrice}
>= #{minPrice}
<= #{maxPrice}
AND (
EXISTS ( SELECT 1 FROM ROOM_FACILITY RF JOIN FACILITY F ON RF.FAC_SEQ = F.FAC_SEQ WHERE RF.ROOM_SEQ = A.ROOM_SEQ AND F.FAC_NAME = #{fac} )
)
ORDER BY ROOM_SEQ DESC ) INNER ) OUTER WHERE RNUM >= #{startRow} AND RNUM <= #{endRow}
