mapper.select:
SELECT ROOM_SEQ, ROOM_CAT_SEQ, HOST_ID, ROOM_ADDR, LATITUDE, LONGITUDE, REGION, REG_DT, UPDATE_DT, AUTO_CONFIRM_YN, ROOM_TITLE, ROOM_DESC, CANCEL_POLICY, AVERAGE_RATING, REVIEW_COUNT, MIN_TIMES, MAX_TIMES, (SELECT NVL(MIN(C2.AMT), 0) FROM ROOM_TYPE T2, ROOM_AMOUNT C2 WHERE T2.ROOM_TYPE_SEQ = C2.ROOM_TYPE_SEQ AND T2.ROOM_SEQ = OUTER.ROOM_SEQ) AMT, (SELECT ROOM_IMG_NAME FROM ROOM_IMAGE WHERE ROOM_SEQ = OUTER.ROOM_SEQ AND IMG_TYPE = 'main') ROOM_IMAGE_NAME FROM ( SELECT ROWNUM RNUM, INNER.* FROM ( SELECT ROOM_SEQ, NVL(ROOM_CAT_SEQ, 0) ROOM_CAT_SEQ, HOST_ID, NVL(ROOM_ADDR, '') ROOM_ADDR, NVL(LATITUDE, 0) LATITUDE, NVL(LONGITUDE, 0) LONGITUDE, NVL(REGION, '') REGION, TO_CHAR(REG_DT, 'YYYY.MM.DD HH24:MI:SS') REG_DT, TO_CHAR(UPDATE_DT, 'YYYY.MM.DD HH24:MI:SS') UPDATE_DT, NVL(AUTO_CONFIRM_YN, '') AUTO_CONFIRM_YN, NVL(ROOM_TITLE, '') ROOM_TITLE, NVL(ROOM_DESC, '') ROOM_DESC, NVL(CANCEL_POLICY, '') CANCEL_POLICY, NVL(AVERAGE_RATING, 0) AVERAGE_RATING, NVL(REVIEW_COUNT, 0) REVIEW_COUNT, NVL(MIN_TIMES, 0) MIN_TIMES, NVL(MAX_TIMES, 0) MAX_TIMES FROM ROOM WHERE 1 = 1
AND ( ROOM_TITLE LIKE '%' || #{searchValue} || '%' OR ROOM_ADDR LIKE '%' || #{searchValue} || '%' OR DBMS_LOB.INSTR(ROOM_DESC, #{searchValue}) > 0 )
AND REGION = #{regionList}
ORDER BY ROOM_SEQ DESC ) INNER ) OUTER WHERE RNUM >= #{startRow} AND RNUM <= #{endRow}
